mapper.update:
UPDATE MEMBERS
SET EMAIL=#{email}, PHONE=#{phone}
WHERE ID=#{id}

mapper.select:
SELECT * FROM ( SELECT rownum AS R, T.* FROM (select * from members
where id like '%'||#{find}||'%'
where name like '%'||#{find}||'%'
where authority=#{find}
ORDER BY reg_date DESC) T ) WHERE R BETWEEN #{startNo} AND #{endNo}
